    public double degreeToRadians(double degree) {
        return (degree * Math.PI) / 180;
    }

    public ArrayList<com.mygdx.game.Texture> loadTextures() {
        ArrayList<com.mygdx.game.Texture> textures = new ArrayList<>();
        File folder =  new File("/home/nerd/Downloads/raycasting/assets");
        File[] files = folder.listFiles();
        textures.add(new com.mygdx.game.Texture());
        assert files != null;
        for (File file : files) {
            com.mygdx.game.Texture myTexture = null;
            if (file.isFile()) {
                Texture badTexture = new Texture(file.getAbsolutePath());
                if (!badTexture.getTextureData().isManaged()) {
                    badTexture.getTextureData().prepare();
                }
                myTexture = new com.mygdx.game.Texture(badTexture);
                textures.add(myTexture);

            }
            assert myTexture != null;
            System.out.println("love is love");
        }

        return textures;
    }



